import React from 'react';
import './App.css';
import Navigation from './Components/Navigation/Navigation.js'
import 'tachyons';
import Logo from './Components/Logo/Logo.js'
import Particles from 'react-particles-js';
import SignIn from './Components/SignIn/SignIn.js'
import Register from './Components/Register/Register.js'
import Home from './Components/Home/Home.js'
import QuizCreate from './Components/QuizCreate/QuizCreate.js'

const particlesConfig={
  particles:{
    number:{
      value:120,
      density:{
        enable:true,
        value_are:300
      }

      }
  }

}








class App extends React.Component {

constructor(){
  super();
  this.state={
    route:'signIn',
    logedIn:false,

     user: {
        id: '',
        username: '',
        email: '',
        score: 0
      }


  }

}


componentDidMount(){
  fetch('http://localhost:3000')
  .then(response=>response.json())
  .then(data=>console.log(data))
}


getUser = (data) => {
    this.setState({user: {
      id: data.id,
      username: data.username,
      email: data.email,
      score: data.score,
    }})
  }




onRouteChange = (newRoute)=>{
  console.log(newRoute)
  if(newRoute==='signout'){
    this.setState({logedIn: false})
    this.setState({user: {
        id: '',
        name: '',
        email: '',
        score: 0
      }})
  }
  else if (newRoute === 'home') {
    this.setState({logedIn: true})
    this.setState({route: 'home'})
  }else if(newRoute==='signout'){
    this.setState({route: 'signIn'});
    console.loge('signouuut')
  }
  else {
    this.setState({route: newRoute});
  }
}

render(){

  let dispElem=<SignIn getUser={this.getUser} onRouteChange={this.onRouteChange}/>;
    switch(this.state.route){
      case 'signIn':
        dispElem=<SignIn getUser={this.getUser} onRouteChange={this.onRouteChange}/>
        break;
      case 'register':
        dispElem=<Register getUser={this.getUser} onRouteChange={this.onRouteChange}/>
        break;
      case 'home':
        dispElem=<Home user={this.state.user} onRouteChange={this.onRouteChange}/>
        break;
      case 'quizCreate':
        dispElem=<QuizCreate  />
        break;
      default:
        dispElem=<SignIn getUser={this.getUser} onRouteChange={this.onRouteChange}/>
    }

  return (

    <div className="App">
      <Particles className="particles" params={particlesConfig} /> 

      <Navigation isLogedIn={this.state.logedIn} onRouteChange={this.onRouteChange} />

      <Logo />

      { 
        this.state.route==='signIn'?
            <SignIn getUser={this.getUser} onRouteChange={this.onRouteChange}/>
        :(this.state.route==='register'?
            <Register getUser={this.getUser} onRouteChange={this.onRouteChange}/>
        :(this.state.route==='home'?
            <Home user={this.state.user} onRouteChange={this.onRouteChange}/>
            :
              (this.state.route==='quizCreate'?
                <QuizCreate  />
                : <div> </div>
                )
            )
          )
      }

    </div>
  );
}

}

export default App;
